def solve(n):
    for a in range(2, int(n**0.5) + 2):
        if a * a > n:
            break
        if n % a == 0:
            n2 = n // a
            for b in range(a + 1, int(n2**0.5) + 2):
                if b * b > n2:
                    break
                if n2 % b == 0:
                    c = n2 // b
                    if c > b:
                        return "YES", a, b, c
    return "NO", None, None, None

def main():
    try:
        num_test_cases = int(input())
        for _ in range(num_test_cases):
            n = int(input())
            verdict, a, b, c = solve(n)
            if verdict == "YES":
                print("YES")
                print(a, b, c)
            else:
                print("NO")
    except (IOError, ValueError):
        pass

if __name__ == "__main__":
    main()
